INSERT INTO behavioral_indicators (competency_id, indicator_text, level) SELECT c.id, '2. 실현가능성과 가용성 등의 실제적인 측면에서 의사결정한다', 'general' FROM competencies c JOIN competency_models m ON c.model_id = m.id WHERE m.name = '역량 평가표' AND c.keyword = '의사결정/판단력' AND NOT EXISTS ( SELECT 1 FROM behavioral_indicators bi WHERE bi.competency_id = c.id AND bi.indicator_text = '2. 실현가능성과 가용성 등의 실제적인 측면에서 의사결정한다' );
INSERT INTO behavioral_indicators (competency_id, indicator_text, level) SELECT c.id, '3. 시의적절하고 정확한 결정을 내리고 행동방안을 제시한다', 'general' FROM competencies c JOIN competency_models m ON c.model_id = m.id WHERE m.name = '역량 평가표' AND c.keyword = '의사결정/판단력' AND NOT EXISTS ( SELECT 1 FROM behavioral_indicators bi WHERE bi.competency_id = c.id AND bi.indicator_text = '3. 시의적절하고 정확한 결정을 내리고 행동방안을 제시한다' );
INSERT INTO competencies (model_id, keyword, description) SELECT m.id, '전략적 사고/기획', '내/외부 요인을 장기적이고 포괄적으로 고려하여 문제해결과 의사결정을 하는 역량. 높은 성과를 낼 수 있는 전략을 확인하고 업무 활동 및 프로세스를 체계적으로 계획하고 조직화하여 실행하는 역량' FROM competency_models m WHERE m.name = '역량 평가표' AND NOT EXISTS ( SELECT 1 FROM competencies c WHERE c.model_id = m.id AND c.keyword = '전략적 사고/기획' );
INSERT INTO behavioral_indicators (competency_id, indicator_text, level) SELECT c.id, '1. 시장 및 경영 환경의 동향을 체계적으로 분석한 결과를 토대로 적합한 대응방안을 제시한다', 'general' FROM competencies c JOIN competency_models m ON c.model_id = m.id WHERE m.name = '역량 평가표' AND c.keyword = '전략적 사고/기획' AND NOT EXISTS ( SELECT 1 FROM behavioral_indicators bi WHERE bi.competency_id = c.id AND bi.indicator_text = '1. 시장 및 경영 환경의 동향을 체계적으로 분석한 결과를 토대로 적합한 대응방안을 제시한다' );
INSERT INTO behavioral_indicators (competency_id, indicator_text, level) SELECT c.id, '2. 핵심 비즈니스 이슈를 구체화하고 조직의 이익 창출을 위한 핵심 성공 요건을 밝힌다', 'general' FROM competencies c JOIN competency_models m ON c.model_id = m.id WHERE m.name = '역량 평가표' AND c.keyword = '전략적 사고/기획' AND NOT EXISTS ( SELECT 1 FROM behavioral_indicators bi WHERE bi.competency_id = c.id AND bi.indicator_text = '2. 핵심 비즈니스 이슈를 구체화하고 조직의 이익 창출을 위한 핵심 성공 요건을 밝힌다' );
INSERT INTO behavioral_indicators (competency_id, indicator_text, level) SELECT c.id, '3. 조직 전략 방향에 부합되도록 이해관계자간의 역학 관계를 바탕으로 업무를 조율·조정한다', 'general' FROM competencies c JOIN competency_models m ON c.model_id = m.id WHERE m.name = '역량 평가표' AND c.keyword = '전략적 사고/기획' AND NOT EXISTS ( SELECT 1 FROM behavioral_indicators bi WHERE bi.competency_id = c.id AND bi.indicator_text = '3. 조직 전략 방향에 부합되도록 이해관계자간의 역학 관계를 바탕으로 업무를 조율·조정한다' );