INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (1, 1, '분석적 사고', '타당한 정보를 근거로 논리 정연하게 사고하여 정확하고 의미 있는 결론을 도출하는 역량. 정보를 체계적으로 분석하여 핵심 요소, 상호 연관성, 경향성을 파악해 상황판단, 문제해결, 의사결정 등에 활용하는 역량', 1, 1, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (2, 1, '의사결정/판단력', '체계적 논리와 근거에 따라 요구에 맞는 타당한 결론을 내리는 역량. 객관적인 기준, 구체적인 사례, 잦은 의견 교환과 주의깊은 관찰을 통해 객관적 자료와 근거를 중심으로 의사결정을 하는 역량', 1, 2, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (3, 1, '전략적 사고/기획', '내/외부 요인을 장기적이고 포괄적으로 고려하여 문제해결과 의사결정을 하는 역량. 높은 성과를 낼 수 있는 전략을 확인하고 업무 활동 및 프로세스를 체계적으로 계획하고 조직화하여 실행하는 역량', 1, 3, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (4, 1, '창의적 사고', '관련성이 적은 개념들 속에서 새롭고 독창적인 연계성을 파악해 가치 있는 아이디어를 창출하는 역량. 목표 달성을 위해 새로운 아이디어와 개념을 적용하여 변화를 시도하거나 창의적인 해결안을 도출하는 역량', 1, 4, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (5, 2, '설득/영향력', '정보, 논리 등을 바탕으로 개인 및 집단을 설득해서 동의와 이해를 얻어내는 역량. 자기견해, 계획, 아이디어를 수용하도록 타인으로부터 필요한 지지와 지원을 끌어내고 태도나 의견을 유도하는 역량', 1, 5, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (6, 2, '팀웍/협동', '공통의 목표 달성을 위해 타인과 협조하는 역량. 개인적 관심사보다 팀 목표를 우선시하고, 최종 결과에 대한 팀/부서의 공동책임을 상호 이해하고 공유하여 협조하는 역량', 1, 6, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (7, 2, '프로젝트 관리', '프로젝트의 범위 및 산출물을 정의하고, 효과적 프로세스를 설계하고 실행하는 역량. 우선순위와 시간, 그리고 자원을 효율적으로 배분 관리하는 역량', 1, 7, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (8, 3, '관계형성', '장/단기적 비즈니스 프로세스 및 성과를 향상시키기 위해 고객, 공급자, 협력자 등 중요한 관련 주체들과의 관계를 형성하고 상호 간 신뢰를 구축하는 역량', 1, 8, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (9, 3, '문서작성 및 관리', '보고서나 전달하고자 하는 지식 및 정보를 문서로 체계적으로 표현하며 향후 업무수행에 용이하게 활용하기 위하여 이를 관리하는 역량', 1, 9, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (10, 3, '정보수집 및 활용', '관련업무 및 그 이상의 주제에 대해서 관심을 갖고 여러 방법을 활용하여 정보를 수집하고, 이를 이용 가능한 정보로 가공/활용하는 역량', 1, 10, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (11, 4, '데이터 분석', '마케팅 데이터를 수집, 분석하여 인사이트를 도출하고 의사결정에 활용하는 역량. 정량적 데이터와 정성적 정보를 통합하여 마케팅 전략 수립을 지원하는 역량', 1, 11, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (12, 4, '디지털 마케팅', '디지털 채널과 플랫폼을 활용한 마케팅 전략을 수립하고 실행하는 역량. 데이터 기반의 디지털 마케팅 캠페인을 기획하고 성과를 분석하여 최적화하는 역량', 1, 12, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (13, 4, '브랜드 관리', '브랜드의 가치를 창출하고 유지하며 발전시키기 위한 전략을 수립하고 실행하는 역량. 브랜드 아이덴티티를 일관성 있게 관리하고 브랜드 경험을 최적화하는 역량', 1, 13, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (14, 4, '시장분석', '비즈니스나 조직에 영향을 미칠 수 있는 현재와 미래의 시장 동향, 소비자/고객 정보, 환경 변화, 트렌드 자료 등에 대한 지식을 감지/예측하여 업무에 적용하는 역량', 1, 14, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (15, 4, '창의적 기획', '혁신적이고 차별화된 마케팅 아이디어를 창출하고 이를 실행 가능한 캠페인으로 기획하는 역량. 시장과 고객의 니즈를 반영한 창의적 솔루션을 개발하는 역량', 1, 15, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (16, 5, '분석적 사고', '타당한 정보를 근거로 논리 정연하게 사고하여 정확하고 의미 있는 결론을 도출하는 역량. 정보를 체계적으로 분석하여 핵심 요소, 상호 연관성, 경향성을 파악해 상황판단, 문제해결, 의사결정 등에 활용하는 역량', 1, 16, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (17, 5, '세밀/정확한 일처리', '세세하고 철저하게 개입하여 업무 및 그 결과물을 관리하여 품질수준을 확보하고 정확하게 업무를 처리하는 역량. 업무 목표를 염두에 두면서 세부사항을 정확하고 치밀하게 처리하는 역량', 1, 17, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (18, 6, '전략적 사고', '각종 전략의 수립과정에서 산업환경 변화가 조직의 장기적인 경쟁 우위에 미치는 영향을 체계적으로 분석하고 평가하여 전략 대안을 도출하는 역량', 1, 18, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (19, 6, '체계적 사고', '업무나 상황을 체계적이고 논리적으로 분석하여 전체적인 맥락에서 이해하고 접근하는 역량. 복잡한 문제를 단계별로 분해하여 해결방안을 도출하는 역량', 1, 19, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (20, 7, '고객중심적 사고', '고객의 비즈니스 니즈를 체계적으로 분석하여 체계적으로 이해하는 역량. 고객의 니즈에 맞춰 이를 충족시킬 수 있는 결정적 대안이나 융통성 있게 대응할 수 있는 전략을 설정하는 역량', 1, 20, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (21, 7, '관계형성', '고객, 동료, 협력업체 등 중요한 관련 주체들과의 관계를 형성하고 상호간 신뢰를 구축하는 역량. 장기적 비즈니스 성과 향상에 필요한 네트워크를 발굴하고 활용하는 역량', 1, 21, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (22, 7, '서비스 지향', '고객의 요구를 우선적으로 감안하고, 고객 만족을 위해서 최선을 다하는 자세. 고객 요청이 있는 경우, 적극적으로 경청하여 요구사항을 정확히 파악하여 직접적인 도움이 될 수 있는 양질의 서비스를 제공하는 역량', 1, 22, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (23, 7, '성과지향', '목표한 성과를 달성하기 위해 지속적으로 노력하고 결과에 대한 책임을 지는 역량. 높은 목표를 설정하고 이를 달성하기 위한 전략과 실행력을 발휘하는 역량', 1, 23, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (24, 8, '상황판단 및 대처능력', '다양한 상황에서 적절한 판단을 내리고 효과적으로 대처하는 역량. 예상치 못한 상황이나 위기 상황에서도 신속하고 정확한 판단으로 적절히 대응하는 역량', 1, 24, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (25, 9, '동시다중업무수행', '다양한 업무를 동시에 수행하면서 업무의 진척도와 품질을 유지하는 역량. 여러 상황적 요인으로 인해 새로운 업무가 부과되더라도 기존 업무를 효과적으로 조정하여 새로운 업무를 동시에 처리하는 역량', 1, 25, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (26, 9, '변화관리/주도', '조직의 변화를 주도적으로 이끌어가고 구성원들이 변화에 적응할 수 있도록 지원하는 역량. 변화의 필요성을 인식하고 변화 추진 과정에서 발생하는 저항을 효과적으로 관리하는 역량', 1, 26, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (27, 9, '설득/영향력', '정보, 논리 등을 바탕으로 개인 및 집단을 설득해서 동의와 이해를 얻어내는 역량. 자기견해, 계획, 아이디어를 수용하도록 타인으로부터 필요한 지지와 지원을 끌어내고 태도나 의견을 유도하는 역량', 1, 27, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
