INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (29, 10, '손익마인드', '투입 대비 산출과 비용의 경제성을 기초로 손익을 관리하는 역량. 비용 대비 효과성이 뛰어난 상품이나 서비스의 개발, 손익 구조 개선 등 이윤 창출의 기회를 파악해 내는 역량', 1, 29, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (30, 11, '재무시스템이해능력', '조직의 재무시스템과 프로세스를 이해하고 효과적으로 활용하는 역량. 재무 데이터의 흐름과 구조를 파악하여 업무에 적용하는 역량', 1, 30, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (31, 11, '전문지식 보유 및 활용', '해당 분야의 전문지식을 지속적으로 습득하고 실무에 적용하는 역량. 최신 동향과 기법을 파악하여 업무의 질을 향상시키는 역량', 1, 31, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (32, 12, '고객중심적 사고', '고객의 비즈니스 니즈를 체계적으로 분석하여 체계적으로 이해하는 역량. 고객의 니즈에 맞춰 이를 충족시킬 수 있는 결정적 대안이나 융통성 있게 대응할 수 있는 전략을 설정하는 역량', 1, 32, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (33, 12, '프로세스 개선', '기존 업무 프로세스를 분석하여 비효율적인 요소를 발견하고 개선방안을 도출하는 역량. 시스템적 사고를 바탕으로 전체적인 업무 흐름을 최적화하는 역량', 1, 33, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (34, 13, '세밀/정확한 일처리', '세세하고 철저하게 개입하여 업무 및 그 결과물을 관리하여 품질수준을 확보하고 정확하게 업무를 처리하는 역량. 업무 목표를 염두에 두면서 세부사항을 정확하고 치밀하게 처리하는 역량', 1, 34, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (35, 13, '의사결정/판단력', '체계적 논리와 근거에 따라 요구에 맞는 타당한 결론을 내리는 역량. 객관적인 기준, 구체적인 사례, 잦은 의견 교환과 주의깊은 관찰을 통해 객관적 자료와 근거를 중심으로 의사결정을 하는 역량', 1, 35, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (36, 13, '주도성', '자발적으로 행동하고 기회를 만들어 새로운 일에 적극적으로 도전하는 역량. 현재 상황에 안주하지 않고 더 나은 결과를 위해 적극적으로 행동하고 변화를 추구하는 역량', 1, 36, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (37, 14, '설득/영향력', '정보, 논리 등을 바탕으로 개인 및 집단을 설득해서 인정을 유도하는 역량. 자기견해, 계획, 아이디어를 수용하도록 타인으로부터 필요한 지지와 지원을 끌어내고 태도나 의견에 영향을 미치는 역량', 1, 37, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (38, 14, '의사결정/판단력', '체계적 논리와 근거에 따라 요구에 맞는 타당한 결론을 내리는 역량. 객관적인 기준, 구체적인 사례, 잦은 의견 교환과 주의깊은 관찰을 통해 객관적 자료와 근거를 중심으로 의사결정을 하는 역량', 1, 38, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (39, 14, '전략적 사고/기획', '내/외부 요인을 장기적이고 포괄적으로 고려하여 문제해결과 의사결정을 하는 역량. 높은 성과를 낼 수 있는 전략을 확인하고 업무 활동 및 프로세스를 체계적으로 계획하고 조직화하여 실행하는 역량', 1, 39, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (40, 14, '창의적 사고', '관련성이 적은 개념들 속에서 새롭고 독창적인 연계성을 파악해 가치 있는 아이디어를 창출하는 역량. 목표 달성을 위해 새로운 아이디어와 개념을 적용하여 변화를 시도하거나 창의적인 해결안을 도출하는 역량', 1, 40, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
INSERT INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES (41, 15, '예산운용능력', '조직의 예산을 효율적으로 계획, 실행, 통제하는 역량. 예산의 편성부터 집행, 성과분석까지 전 과정을 체계적으로 관리하는 역량', 1, 41, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
