-- 마케팅 역량
INSERT OR IGNORE INTO competencies (id, job_id, name, definition, is_active, sort_order, created_at, updated_at) VALUES
(11, 4, '데이터 분석', '마케팅 데이터를 수집, 분석하여 인사이트를 도출하고 의사결정에 활용하는 역량. 정량적 데이터와 정성적 정보를 통합하여 마케팅 전략 수립을 지원하는 역량', 1, 11, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(12, 4, '디지털 마케팅', '디지털 채널과 플랫폼을 활용한 마케팅 전략을 수립하고 실행하는 역량. 데이터 기반의 디지털 마케팅 캠페인을 기획하고 성과를 분석하여 최적화하는 역량', 1, 12, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(13, 4, '브랜드 관리', '브랜드의 가치를 창출하고 유지하며 발전시키기 위한 전략을 수립하고 실행하는 역량. 브랜드 아이덴티티를 일관성 있게 관리하고 브랜드 경험을 최적화하는 역량', 1, 13, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(14, 4, '시장분석', '비즈니스나 조직에 영향을 미칠 수 있는 현재와 미래의 시장 동향, 소비자/고객 정보, 환경 변화, 트렌드 자료 등에 대한 지식을 감지/예측하여 업무에 적용하는 역량', 1, 14, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(15, 4, '창의적 기획', '혁신적이고 차별화된 마케팅 아이디어를 창출하고 이를 실행 가능한 캠페인으로 기획하는 역량. 시장과 고객의 니즈를 반영한 창의적 솔루션을 개발하는 역량', 1, 15, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),

-- 영업 역량
(20, 7, '고객중심적 사고', '고객의 비즈니스 니즈를 체계적으로 분석하여 체계적으로 이해하는 역량. 고객의 니즈에 맞춰 이를 충족시킬 수 있는 결정적 대안이나 융통성 있게 대응할 수 있는 전략을 설정하는 역량', 1, 20, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(21, 7, '관계형성', '고객, 동료, 협력업체 등 중요한 관련 주체들과의 관계를 형성하고 상호간 신뢰를 구축하는 역량. 장기적 비즈니스 성과 향상에 필요한 네트워크를 발굴하고 활용하는 역량', 1, 21, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(22, 7, '서비스 지향', '고객의 요구를 우선적으로 감안하고, 고객 만족을 위해서 최선을 다하는 자세. 고객 요청이 있는 경우, 적극적으로 경청하여 요구사항을 정확히 파악하여 직접적인 도움이 될 수 있는 양질의 서비스를 제공하는 역량', 1, 22, '2025-11-06 08:48:57', '2025-11-06 08:48:57'),
(23, 7, '성과지향', '목표한 성과를 달성하기 위해 지속적으로 노력하고 결과에 대한 책임을 지는 역량. 높은 목표를 설정하고 이를 달성하기 위한 전략과 실행력을 발휘하는 역량', 1, 23, '2025-11-06 08:48:57', '2025-11-06 08:48:57');
